<?phpclass Archivo {    public static function Subir() {        		$objRetorno=new stdClass();		$objRetorno->subio=FALSE;				$archivoTmp = $_POST["idUs"]."-".date("Ymd_His") . ".jpg";		$destino = "./tmp/" . $archivoTmp;		if (file_exists("./tmp/" .$_POST["nomArch"])) {			Archivo::Borrar("./tmp/" .$_POST["nomArch"]);		}				if ($_FILES["archivo"]["size"] > 200000) {			$objRetorno->subio = FALSE;			$objRetorno->mensaje = "El archivo es demasiado grande";			return $objRetorno;		}		$esImagen = getimagesize($_FILES["archivo"]["tmp_name"]);		if($esImagen === FALSE) {			$objRetorno->subio = FALSE;			$objRetorno->mensaje = "Solo se permiten IMAGENES.";			return $objRetorno;		}							$tipoArchivo = pathinfo($_FILES["archivo"]["name"], PATHINFO_EXTENSION);		if($tipoArchivo != "jpg" && $tipoArchivo != "jpeg" && $tipoArchivo != "png") {			$objRetorno->subio = FALSE;			$objRetorno->mensaje = "extensiones permitidas JPG, JPEG O PNG .";			return $objRetorno;		}						if (!move_uploaded_file($_FILES["archivo"]["tmp_name"], $destino)) {			$objRetorno->subio = FALSE;			$objRetorno->mensaje = "Ocurrio un error al subir el archivo. No pudo guardarse.";			return $objRetorno;		}		else{			$objRetorno->subio = TRUE;			$objRetorno->imagenPath=$destino;			$objRetorno->nombArch=$archivoTmp;		}					return $objRetorno;	    }    public static function Borrar($path) {				return unlink($path);    }    public static function Mover($pathOrigen, $pathDestino) {				return copy($pathOrigen, $pathDestino);    }}